const rowSize = 15;
const marginSize = 6;
const boxSize = 14;



let strokes = [];


function setup(){

    createCanvas(350, 350);
    noFill();
    stroke(0);
    noLoop();

    for (let i =0; i < 8; i++) {
      strokes.push(i);
    }

}

function draw(){

    background(255);

    translate(boxSize*2, boxSize*2);

    for (let row=0; row < rowSize; row++) {
      for (let col=0; col < rowSize; col++) {
        let x = boxSize*col + marginSize*col;
        let y = boxSize*row + marginSize*row;
  			stroke(x, y, 255)

        push();
        translate(x, y);
        rect(0, 0, boxSize, boxSize);

        let distFromMiddle = max(abs(row - boxSize/2), abs(col - boxSize/2));

        shuffleArray(strokes);
        for (let i = 0; i < distFromMiddle; i++) {
          drawSegment( strokes[i], boxSize, boxSize);
        }
        pop();
      }
    }

}

function drawSegment(i, w, h){

    switch(i) {
    case 0:
      line(0, 0, w, h);
      break;

    case 1:
      line(w, 0, 0, h);
      break;

    case 2:
      line(0, h/2, w, h/2);
      break;

    case 3:
      line(0, h/2, w/2, 0);
      break;

    case 4:
      line(w/2, 0, w, h/2);
      break;

    case 5:
      line(w, h/2, w/2, h);
      break;

    case 6:
      line(w/2, 0, w/2, h);
      break;

    case 7:
      line(w/2, h, 0, h/2);
      break;
    }

}

function shuffleArray(array){

    var currentIndex = array.length, temporaryValue, randomIndex;

    // While there remain elements to shuffle...
    while (0 !== currentIndex) {

      // Pick a remaining element...
      randomIndex = Math.floor(Math.random() * currentIndex);
      currentIndex -= 1;

      // And swap it with the current element.
      temporaryValue = array[currentIndex];
      array[currentIndex] = array[randomIndex];
      array[randomIndex] = temporaryValue;
    }

    return array;

}

function mousePressed(){

  	redraw();

}
