function setup(){
  createCanvas(300,300)
  background(255)
  noStroke()
}

function makeCreature(numLinks, speed, linkRenderer){
  var creature = {
    numLinks:numLinks, 
    speed:speed, 
    linkRenderer:linkRenderer,
    links: []
  }

  return creature
}

function updateLinks(creature = greenCreature){
  var numLinks = creature.links.length

  // get the most recent link  
  if(numLinks > 0){
    var lastLink = creature.links[numLinks -1]
  }else{
    var lastLink = {x:mouseX, y:mouseY}
  }

  // make a new link based on the most recent link and mouse position
  var newX = lastLink.x * (1-creature.speed) + creature.speed * mouseX
  var newY = lastLink.y * (1-creature.speed) + creature.speed * mouseY
  creature.links.push({x:newX, y:newY})

  // remove the oldest link
  if(numLinks > creature.numLinks){
    creature.links.shift()
  }

}

function renderCreature(creature = greenCreature){
  updateLinks(creature)

  // loop through all of the links
  for(var i = 0; i < creature.links.length; i++){

    // get the position of this link in the creature
    var position = i/creature.numLinks

    // render the link
    var link = creature.links[i]
    creature.linkRenderer(link.x, link.y, position)

  }
}


var redCreature = makeCreature(15, .04, renderRedLink)
var greenCreature = makeCreature(20, .3, renderGreenLink)
var blackCreature = makeCreature(20, .02, renderBlackLink)


function draw(){
  background(255)

  renderCreature(blackCreature)
  renderCreature(redCreature)
  renderCreature(greenCreature)
}

function renderBlackLink(x = mouseX, y = mouseY, position = mouseX/width){
  noStroke()

  // get the color based on where this link is positioned in the creature
  let c = lerpColor(color(0, 0, 0), color(255, 255, 255), position)
  fill(c)

  // render the link
  var inversePos = 1- position
  ellipse(x, y, 70*inversePos, 70*inversePos, position * 20)
}

function renderGreenLink(x = mouseX, y = mouseY, position = mouseX/width){
  noStroke()

  // get the color based on where this link is positioned in the creature
  let c = lerpColor(color(0, 80, 150), color(100, 220, 98), position)
  fill(c)

  // get the size based on where the link is positioned in the creature
  var size
  if(position < .5){
    size = 120*position
  }else{
    size = 120*(1-position)
  }

  // render the link
  ellipse(x, y, size)
}

function renderRedLink(x = mouseX, y = mouseY, position = mouseX/width){
  noStroke()
  rectMode(CENTER)

  // get the color based on where this link is positioned in the creature
  let c = lerpColor(color(125, 0, 270), color(255, 0, 0), position)
  fill(c)

  // render the link
  rect(x, y, 50*position, 50*position, position * 10)


}

function renderBlackCreature(){
  background(255)
  renderCreature(blackCreature)
}

function renderGreenCreature(){
  background(255)
  renderCreature(greenCreature)
}

function renderRedCreature(){
  background(255)
  renderCreature(redCreature)
}
