var player, ball, ai, AI;



var AIScore = 0;
var playerScore = 0;



var dots = [];
var dsize = 10;
var txtSize = 30;


function setup(){

   createCanvas(800, 500)
  player = new player();
  ai = new AI();
  ball = new ball();

    for (let y = dsize/2; y<height; y+=dsize*2){
       dots.push(createVector(width/2-dsize/2, y));
    }

}

function draw(){

  background(0);

    noStroke();
    fill(255, 100);
     drawSquares();

     ball.edges();
     ball.update();
     ball.show();

    player.update();
    player.show();

     ai.update();
     ai.show();

    drawScores();


}

function drawScores(){

    let x1 = width/4
    let x2 = width*3/4
    let y = txtSize*1.5;

    noStroke();
    fill(255);
    textAlign(CENTER);
    textSize(txtSize);
    text(playerScore, x1, y);
    text(AIScore, x2, y);

}

function drawSquares(){

    for(let i = 0; i<dots.length; i++){
     let x = dots [i].x;
     let y = dots [i].y;

     rect(x, y, dsize, dsize);
    }

}

function keyPressed(){

    if(key == 'w' || keyCode==UP_ARROW){
      player.up();
    }else if(key == 's' || keyCode==DOWN_ARROW){
       player.down();
    }

}

function keyReleased(){

    if ((key =='w'|| keyCode==UP_ARROW) || (key == 's' || keyCode==DOWN_ARROW)){
     player.stp();

    }

}

function player(){

    this.w = 15;
    this.h = 80;

    this.pos = createVector(this.w*2, height/2-this.h/2);
    this.acc = createVector(0, 0);
    this.spd = 10;
    this.maxSpd = 10;

    this.show = function(){
    noStroke();
    fill(255);
    rect(this.pos.x, this.pos.y, this.w, this.h);
    }

    this.up = function(){this.acc.y-=this.spd;}
      this.down = function(){this.acc.y+=this.spd;}
      this.stp = function(){this.acc.y = 0;}

      this.update = function(){
        this.acc.y = constrain(this.acc.y, -this.maxSpd, this.maxSpd);
        this.pos.add(this.acc);
        this.pos.y = constrain(this.pos.y, 0, height - this.h);
    }

}

