}

function AI(){


    this.w = player.w;
    this.h = player.h;
    this.pos = createVector(width-this.w*3, height/2-this.h/2);
    this.acc = createVector(0, 0);
    this.spd = 10;
    this.maxSpd = 10;

    this.show = function(){
      noStroke();
      fill(255);
      rect(this.pos.x, this.pos.y, this.w, this.h);
    }

    this.update = function(){

       let d1 = dist(ball.pos.x, ball.pos.y, this.pos.x, this.pos.y);
