       let d = (d1+d2)/2;

       this.pos.add(this.acc);
       this.pos.y = constrain(this.pos.y, 0, height-this.h);

       if(d<450) {
         if(ball.pos.y<this.pos.y-this.h/2){
           this.acc.y-=this.spd;
         }else{
           this.acc.y+=this.spd;
         }

         this.acc.y = constrain(this.acc.y, -this.maxSpd, this.maxSpd);
       } else {
         this.acc.y+=random(-this.spd*0.9, this.spd);
     }
    }

}
