function makeCreature(numLinks, speed, linkRenderer){
  var creature = {numLinks:numLinks, speed:speed, linkRenderer:linkRenderer}

  creature.links = []

  return creature
}

function getNextLink(links = [{x:0, y:0}], speed = .8){
  if(links.length === 0){
    return {x:mouseX, y:mouseY}
  }

  lastLink = links[links.length-1]
  var newX = lastLink.x * (1-speed) + speed * mouseX
  var newY = lastLink.y * (1-speed) + speed * mouseY
  return {x:newX, y:newY}
}

function renderCreature(creature){
  if(!creature){
    background(255)
    creature = greenCreature
  }

  creature.links.push(getNextLink(creature.links, creature.speed))

  if(creature.links.length > creature.numLinks){
    creature.links.shift()
  }

  for(var i = 0; i < creature.links.length; i++){
    var link = creature.links[i]
    creature.linkRenderer(link.x, link.y, i/creature.numLinks)
  }
}


var redCreature = makeCreature(15, .04, renderRedLink)
var greenCreature = makeCreature(20, .3, renderGreenLink)
var blackCreature = makeCreature(20, .02, renderBlackLink)


function setup(){
  createCanvas(300,300)
  background(255)
  noStroke()
}

function renderGreenLink(x = mouseX, y = mouseY, position = mouseX/width){
  noStroke()
  let c = lerpColor(color(0, 80, 150),
  color(100, 220, 98), position);
  fill(c)
  var size

  if(position < .5){
    size = 120*position
  }else{
    size = 120*(1-position)
  }

  ellipse(x, y, size)
}

function renderRedLink(x = mouseX, y = mouseY, position = mouseX/width){
  noStroke()
  rectMode(CENTER);
  let c = lerpColor(color(125, 0, 270), color(255, 0, 0), position);
  fill(c)
  rect(x, y, 50*position, 50*position, position * 10);


}

function renderBlackLink(x = mouseX, y = mouseY, position = mouseX/width){
  noStroke()
    let c = lerpColor(color(0, 0, 0), color(255, 255, 255), position)
  fill(c)
  var position = 1- position
  ellipse(x, y, 70*position, 70*position, position * 20)
}

function renderRedCreature(){
  background(255)
  renderCreature(redCreature)
}

function renderBlackCreature(){
  background(255)
  renderCreature(blackCreature)
}

function renderGreenCreature(){
  background(255)
  renderCreature(greenCreature)
}

function draw(){
  background(255)

  renderCreature(blackCreature)
  renderCreature(redCreature)
  renderCreature(greenCreature)
}
