function makeCreature(numLinks, speed, linkRenderer){
  var creature = {numLinks:numLinks, speed:speed, linkRenderer:linkRenderer}

  creature.links = []

  return creature
}

function getNextLink(links = [{x:0, y:0}], speed = .8){
  if(links.length === 0){
    return {x:mouseX, y:mouseY}
  }

  lastLink = links[links.length-1]
  var newX = lastLink.x * (1-speed) + speed * mouseX
  var newY = lastLink.y * (1-speed) + speed * mouseY
  return {x:newX, y:newY}
}

function renderCreature(creature = firstCreature){

  creature.links.push(getNextLink(creature.links, creature.speed))
  if(creature.links.length > creature.numLinks){
    creature.links.shift()
  }

  for(var i = 0; i < creature.links.length; i++){
    var link = creature.links[i]
    creature.linkRenderer(link.x, link.y, i/creature.numLinks)

  }

}


var firstCreature




function setup(){
  createCanvas(400,400)
  background(255);

  firstCreature =  makeCreature(20, .3, renderBasicLink)

  secondCreature =  makeCreature(15, .04, renderRoundedLink)

  thirdCreature =  makeCreature(20, .02, renderReversedLink)



  noStroke()
}

function renderBasicLink(x = mouseX, y = mouseY, position = mouseX/width){
    let c = lerpColor(color(0, 80, 150),
      color(100, 220, 98), position);
  fill(c)
  var size
  if(position < .5){
    size = 120*position
  }else{
    size = 120*(1-position)
  }
  ellipse(x, y, size)

}

function renderRoundedLink(x = mouseX, y = mouseY, position = mouseX/width){
    rectMode(CENTER);
    let c = lerpColor(color(125, 0, 270), color(255, 0, 0), position);
  fill(c)
  rect(x, y, 50*position, 50*position, position * 10);


}

function renderReversedLink(x = mouseX, y = mouseY, position = mouseX/width){

    let c = lerpColor(color(0, 0, 0), color(255, 255, 255), position);
  fill(c)
  var position = 1- position
  ellipse(x, y, 70*position, 70*position, position * 20);


}

function drawThirdCreature(){

  renderCreature(thirdCreature)
}

function drawSecondCreature(){

  renderCreature(secondCreature)
}

function drawFirstCreature(){

  renderCreature(firstCreature)
}

function draw(){
  background(255, 20)

  drawThirdCreature()
  drawFirstCreature()
  drawSecondCreature()
}
