function setup(){
  createCanvas(width, height)
}

function draw(){
  background(220)

  randomSeed(1);
  filledCircle(
    width / 2,
    height / 2,
    width * 0.1,
    10
  )
}

function filledCircle(x=0, y=0, radius=10, lines=10, color='black'){
  for (let i = 0; i < lines; i++) {
    // Generate 2 random angles
    const angle0 = random(-1, 1) * PI * 2;
    const angle1 = random(-1, 1) * PI * 2;

    // Get a 2D 'vector' from each angle
    const v0 = p5.Vector.fromAngle(angle0);
    const v1 = p5.Vector.fromAngle(angle1);

    // Draw the line
    stroke(color);
    strokeWeight(radius * 0.005);
    line(
      x + v0.x * radius, y + v0.y * radius,
      x + v1.x * radius, y + v1.y * radius
    );
  }

}

function circleRay(x=0, y=0, radius=10, color='black'){
  // Generate 2 random angles
  const angle0 = random(-1, 1) * PI * 2;
  const angle1 = random(-1, 1) * PI * 2;

  // Get a 2D 'vector' from each angle
  const v0 = p5.Vector.fromAngle(angle0);
  const v1 = p5.Vector.fromAngle(angle1);

  // Draw the line
  stroke(color);
  strokeWeight(radius * 0.005);
  line(
    x + v0.x * radius, y + v0.y * radius,
    x + v1.x * radius, y + v1.y * radius
  );
}
