function setup(){
  createCanvas(200,200)
}


var dotSize = 20


function dotPattern(){
  background(255, 10)

  for(var i = 0; i < 10; i++) {
    basicDot(mouseX * i, i*20)
  }
}

function sizeDot(x=mouseX, y=mouseY){

  // generate a random size
  noStroke()
  var size = dotSize + random(-15, 5)

  // use it to draw the dot
  fill(86,220,248, size*.05 *255)
  ellipse(x,y,size, size)

}

function basicDot(x=mouseX, y=mouseY){
  noStroke()
  background(255, 10)
  fill(117,250,151)
  ellipse(x, y, dotSize, dotSize)
}

function draw(){
  background(255, 10)

  for(var i = 0; i < 10; i++) {
    if (i % 2 == 0) {
      basicDot(mouseX * i, i*20)
    } else {
      sizeDot(mouseX * i, i*20)
    }
  }
}
