function setup(){
  createCanvas(200,200)
}


//This is an "Anything" Stamp
// Which can be used for global variables

var dotSize = 20;


function dotPattern(){
  background(255, 10)
  for(var i = 0; i < 10; i++){
    basicDot(mouseX, i*20 )
  }


}

function sizeDot(x=mouseX, y=mouseY){

  // generate a random size
  noStroke()
  var size = random(0, 20)

  // use it to draw the dot
  fill(86,220,248, size*.05 *255);
  ellipse(x,y,size, size);

}

function basicDot(x=mouseX, y=mouseY){

  noStroke()
  background(255, 10)
  fill(117,250,151)
  ellipse(x, y, dotSize, dotSize)

}


