stamps.code:
background(255)
fill(117,250,151)
textSize(50);

// display last key pressed.
text(dotSize + ' ' + keyCode, 20, 70)

if (keyCode == 187) { // keyCode for '+'
  dotSize++
} else if (keyCode == 189) { // keyCode for '-'
  dotSize--
}

colorMode(RGB, 255, 255, 255, 1);
background(151, 97, 246, .01);
stroke(255);
strokeWeight(1)

if (mouseIsPressed) {
  line(mouseX, mouseY, pmouseX, pmouseY);
}

background(255, 10)
for(var i = 0; i < 10; i++){
  basicDot(mouseX, i*20 )
}

// generate a random size
noStroke()
var size = random(0, 20)

// use it to draw the dot
fill(86,220,248, size*.05 *255);
ellipse(x,y,size, size);

